mapper.select:
SELECT r, user_type, user_status, user_name, user_id
FROM
(
    SELECT ROWNUM R, user_type, user_status, user_name, user_id
    FROM
    (
        SELECT user_type, user_status, user_name, user_id
        FROM TBL_member
        ORDER BY user_number
    ) D2

WHERE ROWNUM <= #{page} * #{amount}
) WHERE R > (#{page} - 1) * #{amount}
